import React, {useEffect, useLayoutEffect, useState} from 'react'
import {BrowserRouter} from "react-router-dom";
import {useDispatch, useSelector} from "react-redux";
import {Route, Switch, useHistory} from "react-router";
import {Navbar} from "./navbar/Navbar";
import {Login} from "./auth/Login";
import {Registration} from "./auth/Registration";
import './app.css';
import {Home} from "./Home";
import api from "../exampleApi/APIJWT";
import {userLogIn, userLogOut} from "../reducers/userRedicer";
import Toast from "light-toast";


function App() {
    const [loading,setLoading] = useState(false);
    const dispatch = useDispatch();
    const isAuth = useSelector(state=>state.user.isAuth);
    const history = useHistory()
    const [loadCount,setLoadCount] = useState(0);
    useEffect(()=>{
        if (loading) {
            Toast.loading("", ()=>null)
        }else{
            Toast.hide()
        }
    },[loading])
    useEffect(() => {
        // return ()=> {
        if(!isAuth)
        {
            (async () => {
                console.log('go response');
                setLoading(true);
                let refToken = localStorage.getItem('refresh_token');
                console.log('token',refToken)
                if (refToken !== null) {
                        const user = await api.autoLog();
                        console.log('have request', user)
                        if (user) {
                            setLoading(false);

                            dispatch(userLogIn(user));
                        }else{
                            setLoading(false);
                        }

                    // } catch (error) {
                    //     setLoading(false)
                    //     dispatch(userLogOut());
                    //     history.push('/login');
                    //     console.error(error);
                    //     return false
                    // }
                }else{
                    return false
                }
            })();

        }
        // }
    }, []);
    return (
        <BrowserRouter>
            <div className={'app'}>
                <Navbar/>
                <div className="wrap">
                    <Switch>
                        {!isAuth ?
                            <>
                                <Route exact path={'/login'} render={() => <Login/>}/>
                                <Route exact path={'/registration'} render={() => <Registration/>}/>
                            </>:
                            <>
                                <Route exact path={'/'} render={()=><Home/>}/>
                            </>
                        }
                        <Route render={()=><h1>нет такой страницы</h1>}/>
                    </Switch>
                </div>
            </div>
        </BrowserRouter>
    );
};

export default App;
