import React, {useEffect, useState} from 'react'
import {BrowserRouter} from "react-router-dom";
import {useDispatch, useSelector} from "react-redux";
import {Route, Switch, useHistory} from "react-router";
import {Navbar} from "./navbar/Navbar";
import {Login} from "./auth/Login";
import {Registration} from "./auth/Registration";
import './app.css';
import {Home} from "./Home";
import api from "../Api/api";
import APIJWT from "../exampleApi/APIJWT";
import {userLogIn} from "../reducers/userRedicer";
import Toast from "light-toast";


function App() {
    const [loading,setLoading] = useState(false);
    const dispatch = useDispatch();
    const isAuth = useSelector(state=>state.user.isAuth);
    const history = useHistory()
    useEffect(()=>{
        if (loading) {
            Toast.loading("", undefined)
        }else{
            Toast.hide()
        }
    },[loading])
    useEffect(() => {
        const api = new APIJWT();
        if (!isAuth) {
            setLoading(true);
            (() => {
                let accessToken = localStorage.getItem("token");
                if (accessToken) {
                    try {
                        const res = api.autoLog();
                        console.log( res);
                        dispatch(userLogIn( res))
                        setLoading(false);
                    } catch (error) {
                        console.error(error);
                        setLoading(false);
                    }
                }
            })();
        }else{
            setLoading(false);
            Toast.fail('ошибка на сервере',2000);
        }
    }, [isAuth]);
    return (
        <BrowserRouter>
            <div className={'app'}>
                <Navbar/>
                <div className="wrap">
                    <Switch>
                        {!isAuth ?
                        <>
                            <Route exact path={'/login'} render={() => <Login/>}/>
                            <Route exact path={'/registration'} render={() => <Registration/>}/>
                        </>:
                            <>
                            <Route render={()=><h1>нет такой страницы</h1>}/>
                            <Route exact path={'/'} render={()=><Home/>}/>
                            </>
                        }
                    </Switch>
                </div>
            </div>
        </BrowserRouter>
    );
};

export default App;
