import axios from "axios";
import MockAdapter from "axios-mock-adapter";

const mock = new MockAdapter(axios,{delayResponse:3000});
const user = {
    fullName:'Mr. FabiozZz Loucoster',
    birthDay: '20/10/1989',
    phone: '+79996569772',
    email: 'fabiozzz.dev@gmail.com',
}
mock.onPost('/auth/login').reply(200, {user,accessToken: 'TOKEN_ACC'});
mock.onPost('/auth/register').reply(200, {success: 'Ok'});

mock.onPost('/auth/refresh').reply(200,{user});

mock.onGet('/auth/refresh').reply(200, {user});
/**
 * response на логин token & refreshToken
 */


export default  class APIJWT {
    constructor(options={}) {
        this.client = axios.create();

        // this.client.interceptors.request.use(config=>{
        //     const token = localStorage.getItem('token');
