        //         headers:{},
        //         ...config,
        //     }
        //     newConfig.headers.Authorization = `Bearer ${localStorage.getItem('token')}`
        //     return newConfig
        //
        // },error => Promise.reject(error))

    }

    async register(data) {
        return await this.client.post('/auth/register',data).then(res=>res)
    }

    async login({login,password}) {
        return  await this.client.post('/auth/login',{login,password}).then(res=>{
            console.log(res)
            localStorage.setItem('token', res.data.accessToken);
            return res.data.user
        })
    }

    async getProtected() {
        return await this.client.get('/auth/refresh').then(res=>{
            return res.user;
        })
    }
    logout() {
        localStorage.removeItem('token');
    }

    getUsers() {
        return this.client.get('/users').then(({data}) => data);
    }
}
