import React, {useEffect, useState} from 'react';
import {Input} from "../../utils/input/Input";
import './auth.css';
import {useDispatch} from "react-redux";
import {useHistory} from "react-router";
import api from '../../exampleApi/APIJWT';
import {userLogIn} from "../../actions/userActions";
import {toast, Toaster} from "react-hot-toast";

export const Login = () => {
    const dispatch = useDispatch();
    const [email, setEmail] = useState('');
    const [isLoading, setIsLoading] = useState(false);
    const [password, setPassword] = useState('');
    const history = useHistory()

    useEffect(()=>{
        if (isLoading) {
            toast.loading('loading')
        }else{
            toast.remove(toast.loading);
        }
    },[isLoading])

    const handleOnSubmit = async (e) => {
        e.preventDefault()
        setIsLoading(true)
        const res = await api.login({email, password});
        if (res.data.user) {
            setIsLoading(false)
            await toast.success('Success',{duration:2000})
            // dispatch(userLogIn(res.data.user));
            // history.push('/');
        }else{
            setIsLoading(false)
            await toast.error('Error',{duration:2000})

        }
        // const user = await signUser(password, email)
        //
        // dispatch(userLogIn(user));
        // history.push('/')
    };

    return (
        <>
            <Toaster position={'top-center'} toastOptions={
                {style: {
                        margin: '40px',
                        background: '#363636',
                        color: '#fff',
                        zIndex: 1,
                    }
                }
            }/>
            {!isLoading&&
                <div className={'auth'}>
                    <div className={'auth__title'}>Авторизация</div>
                    <form className={'auth__body'}>
                        <Input type={'text'} value={email} setValue={setEmail} placeholder={'Введите email ...'}/>
                        <Input type={'password'} value={password} setValue={setPassword}
                               placeholder={'Введите пароль ...'}/>
                        <button onClick={handleOnSubmit} className={'auth__btn'}>Войти</button>
                    </form>
                </div>
            }
        </>
    );
};
