import React, {useEffect, useState} from 'react';
import {Input} from "../../utils/input/Input";
import './auth.css';
import sales from '../../utils/sales';
import {regUser} from "../../actions/regUser";
import {useHistory} from "react-router";
import Toast from 'light-toast';
import APIJWT from "../../exampleApi/APIJWT";

export const Registration = () => {
    const api = new APIJWT();
    const [isLoading,setIsLoading] = useState(false);
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [fullName, setFullName] = useState('');
    const [phone, setPhone] = useState('');
    const [birthDay, setBirthDay] = useState('');
    const [optionSale,setOptionSale] = useState('')
    const history = useHistory();

    const salesList = sales.map((sale,index)=>(
        <option key={index} value={sale.id}>{sale.value}</option>
    ))
    const handlerOption = (e)=>{
        setOptionSale(e.target.value);
    }
    useEffect(()=>{
        if (isLoading) {
            Toast.loading("", ()=>null);
        }else{
            Toast.hide()
        }
    },[isLoading])
    const handleOnSubmit = async (e) => {
        e.preventDefault();
        setIsLoading(true);
        const data = {
            email,password,fullName,
            phone,birthDay,optionSale
        };
        await api.register(data).then(resp=> {
            setIsLoading(false)
            Toast.success('',2000,()=>{
                history.push('/login')
            })
        }).catch(er=>{
            setIsLoading(false);
            console.log(er);
        })
    };
    return (
        <div className={'auth'}>
            <form action={'/some/action'}>
                <div className={'auth__title'}>Регистрация</div>
                <div className={'auth__body'}>
                    <Input type={'text'} value={fullName} setValue={setFullName} placeholder={'Введите ФИО ...'}/>
                    <Input type={'email'} value={email} setValue={setEmail} placeholder={'Введите email ...'}/>
                    <Input type={'number'} value={phone} setValue={setPhone}
                           placeholder={'Введите Ваш номер телефона ...'}/>
                    <select onChange={handlerOption}  defaultValue={'Откуда узнали'} className={'auth__select'}>
                        <option disabled>Откуда узнали</option>
                        {salesList}
                    </select>
                    <Input type={'date'} value={birthDay} setValue={setBirthDay} placeholder={'Введите дату рождения ...'}/>
                    <Input type={'password'} value={password} setValue={setPassword} placeholder={'Введите пароль ...'}/>
                    <button type={'submit'} onClick={handleOnSubmit} className={'auth__btn'}>Зарегестрироваться</button>
                </div>
            </form>
        </div>
    );
};
