import axios from "axios";
import MockAdapter from "axios-mock-adapter";

const mock = new MockAdapter(axios,{delayResponse:2000});
const user = {
    fullName:'Mr. FabiozZz Loucoster',
    birthDay: '20/10/1989',
    phone: '+79996569772',
    email: 'fabiozzz.dev@gmail.com',
}
mock.onPost('/auth/login').reply(200, {user,accessToken: 'TOKEN_ACC'});
mock.onPost('/auth/register').reply(200, {success: 'Ok'});

mock.onGet('/auth/refresh').reply(200,{user});

mock.onGet('/auth/refresh').reply(200, {user});
/**
 * response на логин token & refreshToken
 */


export default  class APIJWT {
    constructor(options={}) {
        this.client = axios.create();

        this.client.interceptors.request.use(config=>{
            const token = localStorage.getItem('token');
            if (!token) {
                return config
            }

            const newConfig ={
                headers:{},
                ...config,
            }
            newConfig.headers.Authorization = `Bearer ${localStorage.getItem('token')}`
            return newConfig

        },error => Promise.reject(error))

    }

    async register(data) {
        return await this.client.post('/auth/register',data).then(res=>res)
    }

    async login({login,password}) {
        return  await this.client.post('/auth/login',{login,password}).then(res=>{
            console.log(res)
            localStorage.setItem('token', res.data.accessToken);
            return res.data.user
        })
    }

    async autoLog() {
        return await this.client.get('/auth/refresh').then(res=>{
            console.log(res)
            return res.user;
        })
    }
    logout() {
        localStorage.removeItem('token');
    }

    getUsers() {
        return this.client.get('/users').then(({data}) => data);
    }
}
