import React, {useState} from 'react';

export const Steps = ()=> {
    const [step,setStep] = useState(1);
    const handelNextStep = (e) => {
        e.preventDefault();
        setStep(prevState => prevState +1)
    };
    const handelPrevStep = (e)=>{
        e.preventDefault();
        setStep(prevState => prevState - 1)
    }

    return (
        <form action="">
            <input type="text"/>
            <span>шаг {step}</span>
            {step>1&&<button onClick={handelPrevStep}>Назад</button>}
            <button onClick={handelNextStep}>Вперед</button>
        </form>
    );
}
