import React, {useState} from 'react';
import './forget.css'
import {Steps} from "../../../utils/steps/Steps";
import {Input} from "../../../utils/input/Input";

export const ForgetPassword = ()=> {
    const [email,setEmail] = useState('');
    const [codeEmail,setCodeEmail] = useState('');
    const [password,setPassword] = useState({
        pass:'',
        confPass: '',
    });
    const [step,setStep] = useState(1);

    const handleNextStep = (e)=>{
        e.preventDefault();
        setStep(prevState => prevState + 1);
    }
    const handlePrevStep = (e)=>{
        e.preventDefault();
        setStep(prevState => prevState - 1);
    }

    const place = step === 1 ? 'Введите ваш Email ...' : step ===2? 'Введите пароль из письма на почте ...'
        : 'Введите новый пароль'

    return (
        <React.Fragment>
            <form className={'auth'}>

                <Steps step={step}/>
                <Input placeholder={place}/>
                { step <3 ?
                    <>
                        <button onClick={handlePrevStep} disabled={step <= 1 ? true : false}
                                className={'auth__btn'}>Prev Step
                        </button>
                        <button onClick={handleNextStep} className={'auth__btn'}>Next Step</button>
                    </>
                    :
                    <button className={'auth__btn'}>Finish</button>
                }
            </form>
        </React.Fragment>
    );
}
