import axios from "axios";
import MockAdapter from "axios-mock-adapter";

const mock = new MockAdapter(axios);
const user = {
    fullName:'Mr. FabiozZz Loucoster',
    birthDay: '20/10/1989',
    phone: '+79996569772',
    email: 'fabiozzz.dev@gmail.com',
}
mock.onPost('/auth/registr')
    .reply(200, {success: 'Вы успешно зарегестрировались'});
mock.onPost('/auth/login')
    .reply(200, {
        user,
        success: 'Вы успешно зарегестрировались',
        tokens:{
            accessToken:'TOKRN_ACCESS',
            refreshToken: 'TOKEN_REFRESH'
        }});


export const userActions = (data) => {
        return axios.post('/auth/registr', data).then(response => {
            return response
        });
};
export const signUser = (password,email) => {
    return axios.post('/auth/login', {password, email}).then(respponse => {
        localStorage.setItem('refreshToken',respponse.data.tokens.refreshToken)
        return {user:respponse.data.user,success:respponse.data.success}
    });
};
export const authUser = () => {

};
