const LOG_OUT = 'LOG_OUT';
// const TOKEN_SET = 'TOKEN_SET';
const LOG_IN = 'LOG_IN';
const initialState = {
    currentUser:{},
    isAuth: false,
    // token:null
}
export const userReducer = (state = initialState, action) => {
    switch (action.type) {
        case LOG_IN:
            return {
                ...state,
                currentUser: {...action.user},
                isAuth: true
            }
        case LOG_OUT:
            return {
                currentUser: {},
                isAuth: false,
                token:null
            }
        // case TOKEN_SET:
        //     return {
        //         ...state,
        //         token: action.token
        //     }
        default:
            return state
    }
};

export const userLogIn = (user) => ({type: LOG_IN,user});
// export const setToken = (token) => ({type: TOKEN_SET,token});
export const userLogOut = () => ({type: LOG_OUT});
