/**
 * response на логин token & refreshToken
 */

export default class Api {
    constructor(options = {}) {
        this.client = options.client || axios.create();
        this.token = options.token;
        this.refreshToken = options.refreshToken;
        this.refreshRequest = null;

        this.client.interceptors.request.use(
            config => {
                if (!this.token) {
                    return config;
                }
                const newConfig = {
                    ...config,
                };

                newConfig.headers.Authorization = `Bearer ${this.token}`;
                return newConfig;
            },
            e => Promise.reject(e)
        );

        this.client.interceptors.response.use(
            r => r,
            async error => {
                this.refreshToken = localStorage.getItem('refresh_token')
                if (
                    !this.refreshToken ||
                    error.response.status !== 401 ||
                    error.config.retry
                ) {
                    Promise.reject(error);
                }

                if (!this.refreshRequest) {
                    this.refreshRequest = this.client.post("/auth/refresh", {
                        refreshToken: this.refreshToken,
                    });
                }
                const { data } = await this.refreshRequest;
                this.token = data.token;
                localStorage.setItem('refresh_token',data.refreshToken)
                this.refreshToken = data.refreshToken;
                const newRequest = {
                    ...error.config,
                    retry: true,
                };

                return this.client(newRequest);
            }
        );
    }

    getToken() {
        return this.token;
    }

    async login({ login, password }) {
        const res = await this.client.post("/auth/login", { login, password });
        console.log('вызван логин ')
        this.token = res.data.accessToken;
        localStorage.setItem('refresh_token',res.data.refreshToken)
        this.refreshToken = localStorage.getItem('refresh_token');
        return res
    }

    logout() {
        this.token = null;
        this.refreshToken = null;
        localStorage.removeItem('refresh_token')
    }

    async register(data={}) {
        return await this.client.post('/auth/register', data).then(r => r);
    }

    async getUsers() {
        debugger;
        const res = await this.client.get("/users").then((data ) => data);
        debugger
        return res
    }
}
